import React, { useState } from 'react';
import './LoginSignup.css';
import axios from 'axios'; // Import axios for making HTTP requests

const LoginSignup = () => {
    const [action, setAction] = useState("Login"); // Toggle between Login and Sign Up
    const [email, setEmail] = useState(''); // Store email input
    const [password, setPassword] = useState(''); // Store password input
    const [name, setName] = useState(''); // Store name input (for Sign Up)

    // Handle form submission for login/signup
    const handleSubmit = async (e) => {
        e.preventDefault(); // Prevent page reload on submit

        const url = action === "Login" ? 'http://localhost:5000/login' : 'http://localhost:5000/signup';
        const data = action === "Login" 
            ? { email, password } 
            : { name, email, password };

        try {
            const response = await axios.post(url, data);
            console.log(response.data); // Handle response from backend (e.g., display token or message)
            // Handle success, redirect or show appropriate message
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <div className='container'>
            <div className="header">
                <div className="text">{action}</div>
                <div className="underline"></div>
            </div>
            <form onSubmit={handleSubmit} className="inputs">
                {/* Display Name input only for Sign Up */}
                {action === "Sign Up" && (
                    <div className="input">
                        <input 
                            type="text" 
                            placeholder="Name" 
                            value={name} 
                            onChange={(e) => setName(e.target.value)} 
                        />
                    </div>
                )}
                <div className="input">
                    <input 
                        type="email" 
                        placeholder="Email" 
                        value={email} 
                        onChange={(e) => setEmail(e.target.value)} 
                    />
                </div>
                <div className="input">
                    <input 
                        type="password" 
                        placeholder="Password" 
                        value={password} 
                        onChange={(e) => setPassword(e.target.value)} 
                    />
                </div>
                {action === "Login" && (
                    <div className="forget-password">
                        Lost Password? <span>Click Here!</span>
                    </div>
                )}
                <div className="submit-container">
                    <button type="submit" className="submit">
                        {action === "Login" ? "Login" : "Sign Up"}
                    </button>
                </div>
            </form>
            <div className="submit-container">
                <div
                    className={action === "Login" ? "submit gray" : "submit"}
                    onClick={() => setAction("Sign Up")}
                >
                    Sign Up
                </div>
                <div
                    className={action === "Sign Up" ? "submit gray" : "submit"}
                    onClick={() => setAction("Login")}
                >
                    Login
                </div>
            </div>
        </div>
    );
};

export default LoginSignup;
