import React, { useState } from 'react';
import './LoginSignup.css';
import axios from 'axios';

const LoginSignup = () => {
    const [action, setAction] = useState("Login"); // Initial action is Login
    const [email, setEmail] = useState(''); // Store email input
    const [password, setPassword] = useState(''); // Store password input
    const [name, setName] = useState(''); // Store name input (only for Sign Up)
    const [error, setError] = useState(null); // Store error messages (if any)

    // Handle form submission for Sign Up and Login
    const handleSubmit = async (e) => {
        e.preventDefault(); // Prevent page reload on form submit

        const url = action === "Login" ? 'http://localhost:5000/login' : 'http://localhost:5000/signup';
        const data = action === "Login" 
            ? { email, password } 
            : { name, email, password };

        try {
            const response = await axios.post(url, data);
            console.log(response.data); // Handle successful response (token or success message)
            if (action === "Sign Up") {
                setAction("Login"); // After signing up, switch to login form
                setEmail('');
                setPassword('');
                setName('');
            }
        } catch (error) {
            setError(error.response ? error.response.data.error : 'Something went wrong');
            console.error('Error:', error);
        }
    };

    return (
        <div className='container'>
            <div className="header">
                <div className="text">{action}</div>
                <div className="underline"></div>
            </div>

            {/* Display error message if exists */}
            {error && <div className="error">{error}</div>}

            <form onSubmit={handleSubmit} className="inputs">
                {/* Display Name input only for Sign Up */}
                {action === "Sign Up" && (
                    <div className="input">
                        <input 
                            type="text" 
                            placeholder="Name" 
                            value={name} 
                            onChange={(e) => setName(e.target.value)} 
                        />
                    </div>
                )}
                <div className="input">
                    <input 
                        type="email" 
                        placeholder="Email" 
                        value={email} 
                        onChange={(e) => setEmail(e.target.value)} 
                    />
                </div>
                <div className="input">
                    <input 
                        type="password" 
                        placeholder="Password" 
                        value={password} 
                        onChange={(e) => setPassword(e.target.value)} 
                    />
                </div>
                {action === "Login" && (
                    <div className="forget-password">
                        Lost Password? <span>Click Here!</span>
                    </div>
                )}
                <div className="submit-container">
                    <button type="submit" className="submit">
                        {action === "Login" ? "Login" : "Sign Up"}
                    </button>
                </div>
            </form>

            <div className="submit-container">
                <div
                    className={action === "Login" ? "submit gray" : "submit"}
                    onClick={() => setAction("Sign Up")}
                >
                    Sign Up
                </div>
                <div
                    className={action === "Sign Up" ? "submit gray" : "submit"}
                    onClick={() => setAction("Login")}
                >
                    Login
                </div>
            </div>
        </div>
    );
};

export default LoginSignup;
